INSERT INTO `article` VALUES (30, 'Maven学习', '<h3>概念：</h3><p>&nbsp; &nbsp; &nbsp; &nbsp;Maven是一个项目管理工具，它包含一个项目对象模型，一组标准集合，一个项目生命周期，一个依赖管理系统，和用来运行定义在生命周期阶段中插件目标的逻辑。</p><h3>maven项目标准目录结构</h3><p>&nbsp; &nbsp; &nbsp; src/main/java目录 &nbsp;核心代码部分。<br>&nbsp; &nbsp; &nbsp; src/main/resources &nbsp; 配置文件部分。<br>&nbsp; &nbsp; &nbsp; src/test/jave目录 &nbsp; 测试代码部分<br>&nbsp; &nbsp; &nbsp; src/test/resources &nbsp; 测试配置文件<br>&nbsp; &nbsp; &nbsp; src/main/webapp &nbsp; 页面资源，js，css，图片等</p><h3>生命周期</h3><p><strong>清理生命周期</strong><br>&nbsp; &nbsp; &nbsp; &nbsp;mvn &nbsp;clean &nbsp; 清除项目编译信息<br><strong>默认生命周期</strong><br>&nbsp; &nbsp; &nbsp; &nbsp;mvn &nbsp;complie &nbsp; 生成核心代码编译文件<br>&nbsp; &nbsp; &nbsp; &nbsp;mvn &nbsp;test &nbsp; &nbsp;生成测试编译文件和核心代码编译文件<br>&nbsp; &nbsp; &nbsp; &nbsp;mvn &nbsp;package 生成测试编译文件和核心代码编译文件以及将项目打成war包<br>&nbsp; &nbsp; &nbsp; &nbsp;mvn install &nbsp;生成测试编译文件和核心代码编译文件以及将项目打成war包并安装到本地仓库<br>&nbsp; &nbsp; &nbsp; &nbsp;mvn deploy &nbsp;生成测试编译文件和核心代码编译文件以及将项目打成war包并安装到本地仓库，将项目进行发布<br><strong>站点声明周期</strong></p><h3>Maven概念模型图：</h3><p><span tabindex=\"-1\" contenteditable=\"false\" data-cke-widget-wrapper=\"1\" data-cke-filter=\"off\" class=\"cke_widget_wrapper cke_widget_inline cke_widget_image cke_image_nocaption cke_widget_selected\" data-cke-display-name=\"图像\" data-cke-widget-id=\"0\" role=\"region\" aria-label=\" 图像 小部件\"><img  class=\"img-fluid\"  alt=\"\" height=\"683\" data-cke-saved-src=\"https://img-blog.csdnimg.cn/2020032822270153.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L2tvbmdzYW5qaW4=,size_16,color_FFFFFF,t_70\" src=\"https://img-blog.csdnimg.cn/2020032822270153.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L2tvbmdzYW5qaW4=,size_16,color_FFFFFF,t_70\" width=\"1200\" data-cke-widget-data=\"{&amp;quot;hasCaption&amp;quot;:false,&amp;quot;src&amp;quot;:&amp;quot;https://img-blog.csdnimg.cn/2020032822270153.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L2tvbmdzYW5qaW4=,size_16,color_FFFFFF,t_70&amp;quot;,&amp;quot;alt&amp;quot;:&amp;quot;&amp;quot;,&amp;quot;width&amp;quot;:&amp;quot;1200&amp;quot;,&amp;quot;height&amp;quot;:&amp;quot;683&amp;quot;,&amp;quot;lock&amp;quot;:true,&amp;quot;align&amp;quot;:&amp;quot;none&amp;quot;,&amp;quot;classes&amp;quot;:[]}\" data-cke-widget-upcasted=\"1\" data-cke-widget-keep-attr=\"0\" data-widget=\"image\" class=\"cke_widget_element\"><span class=\"cke_reset cke_widget_drag_handler_container\" style=\"background: url(&quot;https://csdnimg.cn/release/blog_editor_html/release1.6.7/ckeditor/plugins/widget/images/handle.png&quot;) rgba(220, 220, 220, 0.5); top: -15px; left: 0px; display: block;\"><img  class=\"img-fluid\"  class=\"cke_reset cke_widget_drag_handler\" data-cke-widget-drag-handler=\"1\" src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" width=\"15\" title=\"点击并拖拽以移动\" height=\"15\" role=\"presentation\" draggable=\"true\"></span><span class=\"cke_image_resizer\" title=\"点击并拖拽以改变尺寸\">​</span></span></p>', '2020-06-02 15:41:05', 6, 2, 1);
